```javascript
function deepClone(obj, hash = new WeakMap()) {
// 1. nullundefinedSymbol
  if (obj === null || typeof obj !== 'object') {
    return obj;
  }

// 2. 
  if (hash.has(obj)) {
    return hash.get(obj);
  }

// 3. 
  if (obj instanceof Date) {
    return new Date(obj);
  }

  if (obj instanceof RegExp) {
    return new RegExp(obj.source, obj.flags);
  }

  if (obj instanceof Function) {
// 
    return obj;
  }

  if (obj instanceof Map) {
    const mapClone = new Map();
    hash.set(obj, mapClone);
    obj.forEach((value, key) => {
      mapClone.set(deepClone(key, hash), deepClone(value, hash));
    });
    return mapClone;
  }

  if (obj instanceof Set) {
    const setClone = new Set();
    hash.set(obj, setClone);
    obj.forEach(value => {
      setClone.add(deepClone(value, hash));
    });
    return setClone;
  }

// 4. 
  const clone = Array.isArray(obj) ? [] : Object.create(Object.getPrototypeOf(obj));
  hash.set(obj, clone);

// 5.  Symbol 
  const symbolKeys = Object.getOwnPropertySymbols(obj);
  symbolKeys.forEach(symKey => {
    clone[symKey] = deepClone(obj[symKey], hash);
  });

// 6. 
  const keys = Object.keys(obj);
  keys.forEach(key => {
    clone[key] = deepClone(obj[key], hash);
  });

  return clone;
}
```

